		@Override
		public void initialize(final MailListConstraint arg0) {
		}

		@Override
		public boolean isValid(final String mails,
				final ConstraintValidatorContext ctx) {
			if (mails != null) {
				for (String mail : mails.split("[\\s,]")) {
					mail = mail.trim();
					if (!MAIL_PATTERN.matcher(mail).matches()) {
						logger.debug("Mail isn't valid: {}", mail);
						return false;
					}
				}
				return true;
			} else {
				return false;
			}
		}
